(function(){

  var Home = {};

  /* ===========================================================================
  INIT
  =========================================================================== */
  $(document).on('ready', function(){

    Home.banners();
    Home.slidePreloader();
    Home.llamadoRevision();
    Home.sonidoIntro();

  });

   /* ===========================================================================
  AUDIO INTRO
  =========================================================================== */
  Home.sonidoIntro = function(){

    var cargador = new createjs.LoadQueue();
    createjs.Sound.alternateExtensions = ["mp3"];
    cargador.installPlugin(createjs.Sound);
    cargador.on("complete", cargaSonidoCompletado, this);
    cargador.loadManifest([
      {id:"sfx_intro", src:"sound/intro.ogg"}
    ]);

    function cargaSonidoCompletado(){
      var sfxIntro = createjs.Sound.play("sfx_intro", {loop:0});
      sfxIntro.volume = 0.1;
    }
  };

  /* ===========================================================================
  PRECARGA DE IMGENES
  =========================================================================== */
  Home.slidePreloader = function(){
    
    var slidesWrapper = $('#home-main-slide');
    var listaImagenes = [];

    slidesWrapper.find('li').each(function(i, val){
      var pathImg = $(val).find('img').attr('src');

      listaImagenes.push({id:i, src:pathImg});
    });

    var cargadorSlides = new createjs.LoadQueue();
    cargadorSlides.on('fileload', handleFileLoad);
    //cargadorSlides.on('complete', handleComplete);
    cargadorSlides.loadManifest(listaImagenes);

    var contSlides = 0;
    function handleFileLoad(e){
      //console.log(e);

      if(contSlides == 2){

        Home.slideInit();
        $('#home-slide-preloader').fadeOut(1000, function(){
          $('#home-slide').fadeIn(500);
        });
      }
      contSlides++;
    }

    //function handleComplete(){}

  };


  /* ===========================================================================
  LIGHTBOX LLAMADO A REVISIN
  =========================================================================== */
  Home.llamadoRevision = function(){

    $("#home-banner-llamadorevision").fancybox({
      maxWidth  : 600,
      maxHeight : 600,
      fitToView : false,
      height    : '70%',
      width     : '90%',
      autoSize  : true,
      closeClick  : false,
      openEffect  : 'none',
      closeEffect : 'none',
      padding: 0
    });

  };

  /* ===========================================================================
  SLIDES
  =========================================================================== */
  Home.slideInit = function(){

    /* ===========================================================================
    VARIABLES A REUSAR
    =========================================================================== */
    var timerAutoSlide;

    var mainSlide = $('#home-main-slide');
    var prevThumbnailButton = $('#prevThumbnail');
    var nextThumbnailButton = $('#nextThumbnail');

    var thumbnailsWrapper = $('#thumbnails-slide');
    var thumbnail = $('.HomeSlide-thumbnail');
    var numThumbnails = $('.HomeSlide-thumbnail').length;
    var movSpeed = 600;

    /* ===========================================================================
    SLIDE PRINCIPAL
    =========================================================================== */

    /* ===========================================================================
    AUTO START
    =========================================================================== */
    function startAutoSlide(){
      timerAutoSlide = setInterval(function(){
        nextThumbnailButton.trigger('click');
      }, 4000);
    }

    setTimeout(function(){
      startAutoSlide();
    }, 2000);


    /* ===========================================================================
    ANCHO DINMICO DEL CONTENEDOR DE MINIATURAS
    =========================================================================== */
    thumbnailsWrapper.width( (numThumbnails+1) * 211);

    /* ===========================================================================
    EVENTOS PARA NAVEGAR EL SLIDE MINIATURAS
    =========================================================================== */
    function addNavPagerEvents(){

      thumbnail.on('click', function(){
        var self = $(this);
        removeAllEvents();

        //Activamos el thumbnail clickeado
        var thumbnailActivo = $('#thumbnails-slide .active');
        thumbnailActivo.removeClass('active');
        activeThumbnail(self);

      });

      prevThumbnailButton.on('click', function(){
        var self = $(this);

        removeAllEvents();
        moveThumbnailsSlide(self);

        //Activamos el anterior thumbnail
        var thumbnailActivo = $('#thumbnails-slide .active');
        thumbnailActivo.removeClass('active');
        var thumbnailToActive = thumbnailActivo.prev();
        activeThumbnail(thumbnailToActive);

      });

      nextThumbnailButton.on('click', function(){
        var self = $(this);
        removeAllEvents();
        moveThumbnailsSlide(self);

        //Activamos el siguiente thumbnail
        var thumbnailActivo = $('#thumbnails-slide .active');
        thumbnailActivo.removeClass('active');
        var thumbnailToActive = thumbnailActivo.next();
        activeThumbnail(thumbnailToActive);

      });
    }
    addNavPagerEvents();

    /* ===========================================================================
    MOVER EL SLIDE DE MINIATURAS
    =========================================================================== */
    function moveThumbnailsSlide(button){
      //Proceso de movimiento
      var buttonName = button.attr('id');

      if(buttonName === 'prevThumbnail'){

        var lastThumbnail = thumbnailsWrapper.children('.HomeSlide-thumbnail').last();
        lastThumbnail.prependTo(thumbnailsWrapper);
        thumbnailsWrapper.css('left','-211px');

        thumbnailsWrapper.animate({
          'left': 0
        },movSpeed);

      }else if(buttonName === 'nextThumbnail'){
        thumbnailsWrapper.animate({
          'left': -211
        },movSpeed, function(){

          //Movemos la miniatura que qued oculta a la izquierda hacia el final de miniaturas
          thumbnailsWrapper.children('.HomeSlide-thumbnail').first().appendTo(thumbnailsWrapper);
          thumbnailsWrapper.css('left','0px');

        });
      }
    }

    /* ===========================================================================
    REMOVER TODOS LOS EVENTOS DE CAMBIO DE SLIDE
    =========================================================================== */
    function removeAllEvents(){
      //Resteamos el autoslide
      clearInterval(timerAutoSlide);

      thumbnail.off('click');
      prevThumbnailButton.off('click');
      nextThumbnailButton.off('click');
    }

    /* ===========================================================================
    ACTIVAR EL THUMBNAIL: CLICKEADO, ANTERIOR O SIGUIENTE
    =========================================================================== */
    function activeThumbnail(thumbnail) {
      thumbnail.addClass('active');
      var index = thumbnail.attr('data-slide');

      //Mostrar la imagen del slide principal segn el index
      var imgToHide = mainSlide.find('li.active');
      var imgToShow = mainSlide.children('li[data-slide="'+ index +'"]');

      //Ocultamos el activo anterior
      imgToHide.removeClass('active').fadeTo(600, 0);

      //Mostramos el nuevo slide
      imgToShow.fadeTo(600, 1, function(){
        imgToHide.removeClass('active');
        imgToShow.addClass('active');
        addNavPagerEvents();

        //Activamos nuevamente el autoSlide
        startAutoSlide();

      });
    }

  };

  /* ===========================================================================
  BANNERS
  =========================================================================== */
  Home.banners = function(){
    $('#home-banner-slide').bxSlider({
      prevSelector:'.HomeBanners-prevButton',
      nextSelector:'.HomeBanners-nextButton',
      minSlides: 2,
      maxSlides: 4,
      slideWidth: 220,
      slideMargin: 15,
      auto: true,
      moveSlides: 2,
      speed: 900
    });
  };

})();
